(1, 'Category Thumbnail by Plaza Themes', 'category_thumbnail', 'Kazu - Plaza Theme', '1.0', 'http://www.plazathemes.com/', '<modification>\r\n    <name>Category Thumbnail by Plaza Themes</name>\r\n	<version>1.0</version>\r\n	<link>http://www.plazathemes.com/</link>\r\n	<author>Kazu - Plaza Theme</author>\r\n	<code>category_thumbnail</code>\r\n\r\n	<file path="admin/controller/common/menu.php">\r\n		<operation>\r\n			<search><![CDATA[$data[''category''] = $this->url->link(''catalog/category'', ''token='' . $this->session->data[''token''], ''SSL'');]]></search>\r\n			<add position="before"><![CDATA[\r\n				$data[''category_thumbnail''] = $this->url->link(''catalog/occategorythumbnail'', ''token='' . $this->session->data[''token''], ''SSL'');\r\n			]]></add>\r\n		</operation>\r\n		<operation>\r\n			<search><![CDATA[$data[''text_category''] = $this->language->get(''text_category'');]]></search>\r\n			<add position="before"><![CDATA[\r\n				$data[''text_category_thumbnail''] = $this->language->get(''text_category_thumbnail'');\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n	<file path="admin/language/english/common/menu.php">\r\n		<operation>\r\n			<search><![CDATA[''Categories'';]]></search>\r\n			<add position="before"><![CDATA[\r\n				$_[''text_category_thumbnail''] = ''Category Thumbnail'';\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n	<file path="admin/view/template/common/menu.tpl">\r\n		<operation>\r\n			<search><![CDATA[<li><a href="<?php echo $category; ?>"><?php echo $text_category; ?></a></li>]]></search>\r\n			<add position="replace"><![CDATA[\r\n				<li><a class="parent"><?php echo $text_category; ?></a>\r\n					<ul>\r\n						<li><a href="<?php echo $category; ?>"><?php echo $text_category; ?></a></li>\r\n						<li><a href="<?php echo $category_thumbnail; ?>"><?php echo $text_category_thumbnail; ?></a></li>\r\n					</ul>\r\n				</li>\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n</modification>', 1, '2015-10-19 10:42:37'),
(2, 'Product Rotator Image by Plaza Themes', 'product_rotator', 'Kazu - Plaza Theme', '1.0', 'http://www.plazathemes.com/', '<modification>\r\n    <name>Product Rotator Image by Plaza Themes</name>\r\n	<version>1.0</version>\r\n	<link>http://www.plazathemes.com/</link>\r\n	<author>Kazu - Plaza Theme</author>\r\n	<code>product_rotator</code>\r\n\r\n    <!-- Hook into admin product controller -->\r\n	<file path="admin/controller/catalog/product.php">\r\n		<operation>\r\n			<search ><![CDATA[public function index() {]]></search>\r\n			<add position="after"><![CDATA[\r\n				/* Product Rotator */\r\n                $this->load->model(''catalog/ocproductrotator'');\r\n        \r\n                $this->model_catalog_ocproductrotator->installProductRotator();\r\n			]]></add>\r\n		</operation>\r\n        <operation>\r\n            <search ><![CDATA[$data[''entry_image''] = $this->language->get(''entry_image'');]]></search>\r\n            <add position="after"><![CDATA[\r\n				/* Product Rotator */\r\n                $this->load->language(''module/ocproductrotator'');\r\n                $data[''entry_is_rotator''] = $this->language->get(''entry_is_rotator'');\r\n			]]></add>\r\n        </operation>\r\n        <operation>\r\n            <search ><![CDATA[''sort_order'' => $product_image[''sort_order'']]]></search>\r\n            <add position="before"><![CDATA[\r\n                /* Product Rotator */\r\n                ''is_rotator'' => $product_image[''is_rotator''],\r\n            ]]></add>\r\n        </operation>\r\n	</file>\r\n\r\n    <!-- Hook into admin product model -->\r\n    <file path="admin/model/catalog/product.php">\r\n        <operation>\r\n            <search><![CDATA[$this->db->query("INSERT INTO " . DB_PREFIX . "product_image SET product_id = ''" . (int)$product_id . "'', image = ''" . $this->db->escape($product_image[''image'']) . "'', sort_order = ''" . (int)$product_image[''sort_order''] . "''");]]></search>\r\n            <add position="replace">\r\n                <![CDATA[\r\n                    /* Product Rotator */\r\n                    $this->db->query("INSERT INTO " . DB_PREFIX . "product_image SET product_id = ''" . (int)$product_id . "'', image = ''" . $this->db->escape($product_image[''image'']) . "'', sort_order = ''" . (int)$product_image[''sort_order''] . "'', is_rotator = ''" . (int)$product_image[''is_rotator''] . "''");\r\n                ]]>\r\n            </add>\r\n        </operation>\r\n    </file>\r\n\r\n    <!-- Modify admin product form -->\r\n    <file path="admin/view/template/catalog/product_form.tpl">\r\n        <operation>\r\n            <search><![CDATA[<td class="text-right"><?php echo $entry_sort_order; ?></td>]]></search>\r\n            <add position="after">\r\n                <![CDATA[\r\n                    <!-- Product Rotator -->\r\n                    <td class="text-center"><?php echo $entry_is_rotator; ?></td>\r\n                ]]>\r\n            </add>\r\n        </operation>\r\n        <operation>\r\n            <search><![CDATA[<td class="text-left"><button type="button" onclick="$(''#image-row<?php echo $image_row; ?>'').remove();" data-toggle="tooltip" title="<?php echo $button_remove; ?>" class="btn btn-danger"><i class="fa fa-minus-circle"></i></button></td>]]></search>\r\n            <add position="before">\r\n                <![CDATA[\r\n                    <!-- Product Rotator -->\r\n                    <td class="text-center">\r\n                    <select name="product_image[<?php echo $image_row; ?>][is_rotator]" class="form-control rotator-select">\r\n                      <?php if(isset($product_image[''is_rotator'']) && (int) $product_image[''is_rotator''] == 1): ?>\r\n                        <option value="1" selected="selected">Yes</option>\r\n                        <option value="0">No</option>\r\n                      <?php else: ?>\r\n                        <option value="1">Yes</option>\r\n                        <option value="0" selected="selected">No</option>\r\n                      <?php endif; ?>\r\n                    </select>\r\n                    </td>\r\n                    <!-- End -->\r\n                ]]>\r\n            </add>\r\n        </operation>\r\n        <operation>\r\n            <search><![CDATA[function addImage() {]]></search>\r\n            <add position="before">\r\n                <![CDATA[\r\n                    //Product Rotator\r\n                    $(''#tab-image tfoot td:first'').attr(''colspan'', 3);\r\n                    \r\n                    $(''.rotator-select'').change(function() {\r\n                      var value = $(this).val();\r\n                      if(value == 1) {\r\n                        $(''.rotator-select'').val(0);\r\n                        $(this).val(1);\r\n                      }\r\n                    });\r\n                    //End\r\n                ]]>\r\n            </add>\r\n        </operation>\r\n        <operation>\r\n            <search><![CDATA[$(''#images tbody'').append(html);]]></search>\r\n            <add position="after">\r\n                <![CDATA[\r\n                    //Product Rotator\r\n                    $(''.rotator-select'').change(function() {\r\n                    var value = $(this).val();\r\n                    if(value == 1) {\r\n                      $(''.rotator-select'').val(0);\r\n                      $(this).val(1);\r\n                    }\r\n                    });\r\n                    //End\r\n                ]]>\r\n            </add>\r\n        </operation>\r\n        <operation>\r\n            <search><![CDATA[html += ''  <td class="text-left"><button type="button" onclick="$(\\''#image-row'' + image_row  + ''\\'').remove();" data-toggle="tooltip" title="<?php echo $button_remove; ?>" class="btn btn-danger"><i class="fa fa-minus-circle"></i></button></td>'';]]></search>\r\n            <add position="before">\r\n                <![CDATA[\r\n                    //Product Rotator\r\n                    html += '' <td class="text-center">'';\r\n                    html += ''   <select name="product_image['' + image_row + ''][is_rotator]" class="form-control rotator-select">'';\r\n                    html += ''     <option value="1">Yes</option>'';\r\n                    html += ''     <option value="0" selected="selected">No</option>'';\r\n                    html += ''   </select>'';\r\n                    html += '' </td>'';\r\n                    //End\r\n                ]]>\r\n            </add>\r\n        </operation>\r\n    </file>\r\n\r\n</modification>', 1, '2015-10-19 11:06:37'),
(3, 'Blog For Opencart 2.x+', 'easy_blog', 'CarAn', '1.0', '', '<?xml version="1.0" encoding="utf-8"?>\r\n<modification>\r\n    <code>easy_blog</code>\r\n    <name>Blog For Opencart 2.x+</name>\r\n    <version>1.0</version>\r\n    <author>CarAn</author>\r\n    <file path="admin/controller/common/menu.php">\r\n        <operation>\r\n            <search trim="true" index="0"><![CDATA[\r\n				return $this->load->view(''common/menu.tpl'', $data);\r\n            ]]></search>\r\n            <add position="before" trim="false" offset="0"><![CDATA[\r\n				$data[''text_blog''] = $this->language->get(''text_blog'');\r\n				$data[''text_blog_article''] = $this->language->get(''text_blog_article'');\r\n				$data[''blog_article''] = $this->url->link(''blog/article'', ''token='' . $this->session->data[''token''], ''SSL'');\r\n            ]]></add>\r\n        </operation>\r\n    </file>\r\n	\r\n	<file path="admin/view/template/common/menu.tpl">\r\n        <operation>\r\n            <search trim="true" index="0"><![CDATA[\r\n				<li id="system"><a class="parent"><i class="fa fa-cog fa-fw"></i> <span><?php echo $text_system; ?></span></a>\r\n            ]]></search>\r\n            <add position="before" trim="false" offset="0"><![CDATA[\r\n				<li id="blog"><a class="parent"><i class="fa fa-pencil-square-o fa-fw"></i> <span><?php echo $text_blog; ?></span></a>\r\n					<ul>\r\n						<li><a href="<?php echo $blog_article; ?>"><?php echo $text_blog_article; ?></a></li>\r\n					</ul>\r\n				</li>\r\n            ]]></add>\r\n        </operation>\r\n    </file>\r\n	\r\n	<file path="admin/language/english/common/menu.php">\r\n        <operation>\r\n            <search trim="true" index="0"><![CDATA[\r\n				$_[''text_user'']                        = ''Users'';\r\n            ]]></search>\r\n            <add position="before" trim="false" offset="0"><![CDATA[\r\n				$_[''text_blog'']                        = ''Blog'';\r\n				$_[''text_blog_article'']                = ''Articles'';\r\n       